import React, { useState } from "react";
import "./counter.css";

function Counter(props){
    let [count, updateCount] = useState(0);

    function handleMinus() {
        updateCount(count - 1);
    }
    function handlePlus() {
        updateCount(count + 1);
    }

    return(
        <div>
            <button onClick={handleMinus}>-</button>
            <p>{count}</p>
            <button onClick={handlePlus}>+</button>
        </div>
    )
}


export default Counter;
